                count++;

                int currentShape = shapeCounter % 5;
                shapeCounter++;

                (int bottomLeftCornerXStart, int bottomLeftCornerYStart, string[,] shape) = spawnNewShape(grid, currentShape);
                (int bottomLeftCornerX, int bottomLeftCornerY) = iterateFall(grid, jetPattern, bottomLeftCornerXStart, bottomLeftCornerYStart, shape, currentShape);
            }

            List<int> widthList = getWidthList(grid);

            long mod = (widthList.Min() + 1) * jetPattern.Length; // mogelijk geen + 1

            Console.WriteLine("mod = " + mod);

            long div = (long)1000000000000 / (long)mod;
            long rest = (long)1000000000000 % (long)mod;

            Console.WriteLine("div = " + div);
            Console.WriteLine("rest = " + rest);

            // Calculate the height of the remainder
            count = 0;
            currentJetIndex = 0;

            grid = new string[7, (int)(rest * 2)];

            while (count < rest)
            {
                int currentShape = shapeCounter % 5;

                shapeCounter++;

                if (count % 1000 == 0)
                {
                    Console.WriteLine(count);
                }

                (int bottomLeftCornerXStart, int bottomLeftCornerYStart, string[,] shape) = spawnNewShape(grid, currentShape);


                (int bottomLeftCornerX, int bottomLeftCornerY) = iterateFall(grid, jetPattern, bottomLeftCornerXStart, bottomLeftCornerYStart, shape, currentShape);

                count++;
            }

            int restHeight = getHeight3(grid);

            Console.WriteLine($"rest height = {restHeight}");

            // Calculate the height of the mod
            count = 0;
            currentJetIndex = 0;

            grid = new string[7, (int)(mod * 2)];

            while (count < mod)
            {
                int currentShape = shapeCounter % 5;

                shapeCounter++;

                if (count % 1000 == 0)
                {
                    Console.WriteLine(count);
                }

                (int bottomLeftCornerXStart, int bottomLeftCornerYStart, string[,] shape) = spawnNewShape(grid, currentShape);


                (int bottomLeftCornerX, int bottomLeftCornerY) = iterateFall(grid, jetPattern, bottomLeftCornerXStart, bottomLeftCornerYStart, shape, currentShape);

                count++;
            }

            long modHeight = getHeight3(grid);

            Console.WriteLine($"mod height = {modHeight}");

            // Calculate final answer
            long result = restHeight + modHeight * div;
            Console.WriteLine($"result = {result}");
        }

